mapper.select:
SELECT a.disease_name diseaseName, GROUP_CONCAT(a.medicalName SEPARATOR ',') as medicalName FROM (SELECT d.disease_name, m.medicalName FROM disease_test d, medical m WHERE
sympton_code REGEXP #{item}
and d.medical_code REGEXP m.medicalCode) a GROUP BY a.disease_name;

SELECT b.bodyName bodyName FROM body b, body_symptom1 s WHERE b.bodyCode = s.bodyCode
and s.symptomCode = #{item}
GROUP BY b.bodyName

SELECT GROUP_CONCAT(a.typicalIngredient) as typicalIngredient FROM (SELECT typicalIngredient FROM product WHERE
typicalFunction REGEXP #{item}
GROUP BY typicalIngredient) a
